import { useEffect, useState } from 'react';
import ReactDOM from 'react-dom/client';
import styles from './styles/Calculate.css';

const Calculate = () => {
    const [figure, setFigure] = useState('triangle');
    const [side1 , setSide1 ] = useState('1')
    const [side2 , setSide2 ] = useState('1')
    const [height , setHeight ] = useState('1')
    const [base, setBase] = useState('2')
    const inputs = {
        triangle: {
            renderElements: (
                <form  >
                    <input value={side1} id='side1' placeholder='side 1' />
                    <input value={side2} id='side2' placeholder='side 2' />
                    <input value={height} id='height' placeholder='height' />
                    <input value={base} id='base' placeholder='base' />
                    <input type="submit" value='submit' />
                </form>
            ),
            values:{

            }
        },
        square: {
            renderElements: (
                <form>
                    <input placeholder='side'></input>
                    <input type='submit'></input>
                </form>
            ),
        },
    };

    const renderInput = (event) => {
        const value = event.target.value;
        setFigure(value);
        const containerCalc = document.getElementById('calculatorContainer');
        const root = ReactDOM.createRoot(containerCalc);
        root.render(inputs[value].renderElements);
    };

    useEffect(() => {
        const containerCalc = document.getElementById('calculatorContainer');
        const root = ReactDOM.createRoot(containerCalc);
        root.render(inputs[figure].renderElements);
    }, [figure]);

    return (
        <div style={{ display: 'flex', flexDirection: 'row' }}>
            <select
                id='selectFigure'
                value={figure}
                onChange={renderInput}
                style={{ width: '200px', height: '30px' }}
            >
                <option value="square">Square</option>
                <option value="triangle">Triangle</option>
            </select>

            <div id='calculatorContainer'></div>
        </div>
    );
}

export default Calculate;
