    const  inputs   =    {triangle: { renderElements:  <form><input id='side1' placeholder='side 1' /><input id='side2' placeholder='side 2' /> <input id='base' placeholder='base' />   </form>   } }




        const renderInput = (event)=>{
            let value = event.target.value
            let container = document.getElementById('calculatorContainer')
