import { useState } from 'react';
import styles from './styles/Calculate.css';
import ReactDOM from 'react-dom/client';

const Calculate = () => {
    const inputs = {
        triangle: {
            renderElements: (
                <form>
                    <input id='side1' placeholder='side 1' />
                    <input id='side2' placeholder='side 2' />
                    <input id='base' placeholder='base' />
                    <select>
                        <option value="area">Area</option>
                        <option value="perimeter">Perimeter</option>
                    </select>
                    <input type="submit" value='Submit' />
                </form>
            )
        }
    };

    const [selectedShape, setSelectedShape] = useState('triangle');

    const renderInput = (event) => {
        let value = event.target.value;
        setSelectedShape(value); // Update the selected shape
        let container = document.getElementById('calculatorContainer');
        const root = ReactDOM.createRoot(container);
        root.render(inputs[value]?.renderElements || <div>Select an option</div>);
    };

    return (
        <div style={{ display: 'flex', flexDirection: 'row' }}>
            <select value={selectedShape} onChange={renderInput} style={{ width: '200px', height: '30px' }}>
                <option value="someValue">Some Value</option>
                <option value="triangle">Triangle</option>
            </select>
            <div id='calculatorContainer'></div>
        </div>
    );
};

export default Calculate;
