import { useState } from 'react'
import styles from  './styles/Calculate.css'
import ReactDOM from 'react-dom/client';








const Calculate = () =>{





    const  inputs   =    {triangle: { renderElements:  <div><input id='side1' placeholder='side 1' /><input id='side2' placeholder='side 2' /> <input id='base' placeholder='base' /> <select><option placeholder="area">  area  </option> <option placeholder='perimeter'>perimeter</option> </select> </div>   } }




        const renderInput = (event)=>{
            let value = event.target.value
            let container = document.getElementById('calculatorContainer')
            const root = ReactDOM.createRoot(container);
            root.render(inputs[value].renderElements)
        }






    return <div  style={{display: 'flex' , flexDirection: 'row',}}>
      <select  onChange={renderInput}  style={{width: '200px' , height: '30px'  }}>
<option>some shit</option>
<option value={'triangle'}>triangle</option>

      </select>
    <div  id='calculatorContainer'></div>
           </div>;
}

export default Calculate;
